import cfnresponse

def lambda_handler(event, context):
  responseData = {}
  try:
    input_string = str(event['ResourceProperties']['InputString'])
    max_length = int(event['ResourceProperties']['MaxLength'])
    responseData['returnValue'] = input_string[:max_length]
    cfnresponse.send(event, context, 'SUCCESS', responseData)
  except Exception as ex:
    print(ex)
    cfnresponse.send(event, context, 'FAILED', responseData)
